* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: "Trebuchet MS", Trebuchet, Arial, sans-serif;
  background-color: #3B3F45;
  min-height: 100vh;
  border: 15px solid white;
  font-size: 16px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  flex-wrap: nowrap;
}

.header {
  padding: 22px;
  width: 100%;
  background-color: #4A4F56;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  flex-wrap: nowrap;
}
.header__title {
  font-size: 1.5rem;
  font-weight: 700;
  color: #EB7E7E;
  margin-bottom: 4px;
}

.subtitle {
  font-size: 1.125rem;
  font-weight: 500;
  color: #EB7E7E;
}

.main {
  align-self: center;
  padding: 40px;
  width: 100%;
  max-width: 880px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: stretch;
  flex-wrap: nowrap;
}
.main__list {
  margin-bottom: 40px;
}

.shopping-list {
  list-style: none;
}
.shopping-list__item:nth-of-type(odd) {
  background-color: #88839E;
}
.shopping-list__item:nth-of-type(even) {
  background-color: #7A7593;
}

.shopping-item {
  display: block;
  overflow: hidden;
  padding: 16px;
  display: flex;
  flex-direction: raw;
  justify-content: flex-start;
  align-items: center;
  flex-wrap: nowrap;
  cursor: pointer;
  transition: background-color 0.2s ease;
  transition-property: background-color, border-color;
  transition-duration: 0.2s;
  transition-timing-function: ease;
}
.shopping-item:hover {
  background: #625E77;
  border-top: 2px solid white;
  border-bottom: 2px solid white;
}
.shopping-item:active {
  background: #cf8c8c;
}
.shopping-item__input {
  width: 24px;
  height: 24px;
  margin-right: 20px;
  flex-shrink: 0;
}
.shopping-item__input:checked + .shopping-item__content {
  text-decoration: line-through;
}
.shopping-item__input:checked + .shopping-item__content .shopping-item__name {
  color: #9B9AB0;
}
.shopping-item__content {
  display: flex;
  flex-direction: raw;
  justify-content: flex-start;
  align-items: center;
  flex-wrap: nowrap;
}
.shopping-item__name {
  font-size: 1.25rem;
  font-weight: 400;
  color: #fff;
}
.shopping-item__extra {
  font-size: 0.875rem;
  font-weight: 400;
  color: #9B9AB0;
  position: relative;
  top: 1px;
  display: inline-block;
}
.shopping-item__extra:before {
  content: "(";
}
.shopping-item__extra:after {
  content: ")";
}

.footer {
  padding: 22px;
  background-color: #4A4F56;
  text-align: center;
}
